	MEM_ZIPCODE = #{memZipcode}, MEM_ADDR1 = #{memAddr1}, mem_ADDR2 = #{memAddr2},
	MEM_HIREDATE = #{memHiredate}, MEM_POSITION = #{memPosition}, MEM_RES = #{memRes},
	JG_NO = #{jgNo}, DEPT_NO = #{deptNo}
WHERE MEM_NO = #{memNo}
